import React, { Component } from 'react';
import './App.sass';
import data from './data.json'
import Product from './product.js'
import ProductInfo from './productinfo.js'

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {
      prodId:1,
      searchValue:'',
      cartItemcount:0
    };
  }

  productHandler = (e)=>{
    var id = e.target.id
    this.setState({prodId:id});
    console.log("e",e.target,this.state.prodId)
  }

  searchHandler = (e)=>{
    console.log("searchva",e.target.value)
    this.setState({searchValue:e.target.value})
    for(var i of data){
      if(i.name == e.target.value){
        this.setState({prodId:i.id})
      }
    }
  }

  cartHandler = ()=>{
    this.setState({cartItemcount:this.state.cartItemcount+1});
    console.log("cartCount",this.state.cartItemcount)
  }
  render(){
    //console.log("data",data);
    var inputStyle = {
      position:"relative"
    }
    var searchIconStyle = {
      position:"absolute",
       right:"100px", 
       top:"80px"
    }
    var prodInf ;

    if(this.state.prodId === ''){
      prodInf = <div className="productDetails"></div>
    }
    else{
      // console.log("oooo",this.state.prodId.getAttribute('id'))
      let specificProdId = this.state.prodId;
      // let specificProdDetail = data.map(item=>{
      //   console.log("item",item)
      //   if(item.id == specificProdId){
      //     return item
      //   }
      // })
      let specificProdDetail;
      for(var i of data){
        if(i.id == specificProdId){
          specificProdDetail = i;
        }
      }
      console.log("specific",specificProdDetail)
      prodInf = <ProductInfo prodDetails ={specificProdDetail} cartHandler={this.cartHandler}/>
    }

    return(
      <div>
       <div id="header">
         <h3 id ="appTitle">Widget Depot</h3>
         <span style={{fontSize:"24px",float:"right"}} className='fa fa-user-circle userIcon'></span>
        </div>
        <div className="form-group searchDiv">
        <input style={inputStyle} className="form-control "  name="search"  type="text"   placeholder="Find Widget of your Dreams" value={this.state.searchValue} onChange={this.searchHandler}/>
        <span style={searchIconStyle} className="fa fa-search "></span>
      </div>
      <div className="productContainer">
        <div className="products flex-container">
          {
          data.map(prod=>(
            <Product data = {prod} key={prod.id} handler = {this.productHandler}/>
          )
          )}
        </div>

        {prodInf}
      </div>
      </div>


    )
  }
}
export default App;
