import React, { Component } from 'react';
import './App.css';
import data from './data.json'
import Product from './product.js'

class App extends Component{

    state={
        prodId:''
    }
  productHandler = function(e){
    var id = e.target.id
    console.log("e",e.target.id,this.state.prodId)
    //this.setState({prodId:id});
  }
  render(){
    console.log("data",data);
    var inputStyle = {
      position:"relative"
    }
    var searchIconStyle = {
      position:"absolute",
       right:"100px", 
       top:"80px"
    }


    return(
      <div>
       <div id="header">
         <h3 id ="appTitle">Widget Depot</h3>
         <span style={{fontSize:"24px",float:"right"}} className='fa fa-user-circle userIcon'></span>
        </div>
        <div className="form-group searchDiv">
        <input style={inputStyle} className="form-control "  name="search"  type="text"   placeholder="Find Widget of your Dreams" />
        <span style={searchIconStyle} className="fa fa-search "></span>
      </div>
      <div className="productContainer">
        <div className="products flex-container">
          {
          data.map(prod=>(
            <Product data = {prod} key={prod.id} handler = {this.productHandler}/>
          )
          )}
        </div>
        <div className="productDetails">
        <div id="sub-header">
         <span id ="ProdName">Widget Depot</span>
         <span className='fa fa-user-circle addCartIcon' style={{float:"right",fontSize:"20px"}}></span>
        </div>
        <div id="prod-div">

        </div>
        <hr></hr>
        <div id="specifications">
          <span>Specifications</span>
          <div className="flex-container specs">
          <div>1</div>
          <div>2</div>
          <div>3</div>  
          </div>
          <hr></hr>
          <div id ="description">
            <p>hello</p>
          </div>
        </div>
        </div>
      </div>
      </div>


    )
  }
}
export default App;
